mapper.select:
select * from (select rownum rn, A.* from
(select * from commboard order by boardNum desc)A)
where rn between #{startRow} and #{endRow}

	select * from (select rownum rn, A.* from
(select * from commboard where look = #{look} or look = '전체공개' or look ='전체공개,회원공개' order by boardNum desc)A)
where rn between #{startRow} and #{endRow}

	select * from (select rownum rn, A.* from
(select * from commboard where look = #{look} or look = '전체공개' or look ='전체공개,회원공개' or look='비공개' and comm_memberNum=#{comm_memberNum}  
 order by boardNum desc)A)
where rn between #{startRow} and #{endRow}
